/**
 * Class for storing and interacting with an array of colours.
 *
 * NOTE Rename this to ColorSpan
 */
export default class ArraySpan extends Span {
  /**
   * Constructs an ArraySpan instance.
   *
   * @param {string|array} colors - single or multiple colors,
   * if the string 'random' is passed, a random color will be returned
   * from the getValue method
   * @return void
   */
  constructor(colors = 'random') {
    super();

    /**
     * @desc An array of colors
     * @type {array}
     */
    this._arr = Array.isArray(colors) ? colors : [colors];
  }

  /**
   * Gets a color from the color array.
   *
   * @return {string} a hex color
   */
  getValue() {
    const color = _.sample(this._arr);

    return this.isRandomColor(color) ? MathUtils.randomColor() : color;
  }

  /**
   * Determines if the color supplied is 'random'.
   *
   * @param {string} color - the color to check
   */
  isRandomColor(color) {
    return typeof color === 'string' && color.toLowerCase() === 'random';
  }
}

/**
 * Attempts to create an ArraySpan from the colors provided.
 *
 * @param {mixed} colors - colors to try and create an ArraySpan from
 * @return {?ArraySpan}
 */
export const createArraySpan = colors => {
  if (!colors) {
    return null;
  }

  if (colors instanceof ArraySpan) {
    return colors;
  }

  return new ArraySpan(colors);
};

export default class Box {
  constructor(x, y, z, w, h, d) {
    this.x = x;
    this.y = y;
    this.z = z;
    this.width = w;
    this.height = h;
    this.depth = d;
    this.bottom = this.y + this.height;
    this.right = this.x + this.width;
    this.right = this.x + this.width;
  }

  contains(x, y, z) {
    if (
      x <= this.right &&
      x >= this.x &&
      y <= this.bottom &&
      y >= this.y &&
      z <= this.depth &&
      z >= this.z
    )
      return true;
    else return false;
  }
}

import { EULER } from '../constants';
import Util from '../utils/Util';

/**
 *  Numerical integration
 */
export default class Integration {
  constructor(type) {
    this.type = Util.initValue(type, EULER);
  }

  integrate(particles, time, damping) {
    this.euler(particles, time, damping);
  }

  euler(particle, time, damping) {
    if (!particle.sleep) {
      particle.old.p.copy(particle.p);
      particle.old.v.copy(particle.v);
      particle.a.scalar(1 / particle.mass);
      particle.v.add(particle.a.scalar(time));
      particle.p.add(particle.old.v.scalar(time));
      damping && particle.v.scalar(damping);
      particle.a.clear();
    }
  }
}

import { PI } from '../constants';

export default {
  randomAToB: function(a, b, INT) {
    if (!INT) return a + Math.random() * (b - a);
    else return ((Math.random() * (b - a)) >> 0) + a;
  },
  randomFloating: function(center, f, INT) {
    return this.randomAToB(center - f, center + f, INT);
  },

  randomZone: function(display) {}, //eslint-disable-line

  degreeTransform: function(a) {
    return (a * PI) / 180;
  },

  toColor16: function getRGB(num) {
    return '#' + num.toString(16);
  },

  randomColor: function() {
    return (
      '#' +
      ('00000' + ((Math.random() * 0x1000000) << 0).toString(16)).slice(-6)
    );
  },

  lerp: function(a, b, energy) {
    return b + (a - b) * energy;
  },

  getNormal: function(v, n) {
    if (v.x == 0 && v.y == 0) {
      if (v.z == 0) n.set(1, 0, 1);
      else n.set(1, 1, -v.y / v.z);
    } else {
      if (v.x == 0) n.set(1, 0, 1);
      else n.set(-v.y / v.x, 1, 1);
    }

    return n.normalize();
  },

  /**
   * Rodrigues' Rotation Formula
   * https://en.wikipedia.org/wiki/Rodrigues%27_rotation_formula
   * v = vcos() + k(kv)(1cos()) + (k*v)sin()
   */
  axisRotate: function(v0, v, k, tha) {
    var cos = Math.cos(tha);
    var sin = Math.sin(tha);
    var p = k.dot(v) * (1 - cos);

    v0.copy(k);
    v0.cross(v).scalar(sin);
    v0.addValue(v.x * cos, v.y * cos, v.z * cos);
    v0.addValue(k.x * p, k.y * p, k.z * p);
  }
};

